mapper.select:
SELECT product_id
FROM product
WHERE product_name = #{productName};

mapper.insert:
INSERT IGNORE INTO product_material(material_name_id, product_id, weight)
SELECT material_name_id, #{productId}, #{weight}
FROM material_name
WHERE material_name = #{materialName};

mapper.update:
UPDATE IGNORE `product`
SET  description = #{description}
WHERE product_name = #{productName};

UPDATE IGNORE `product_material`
SET  material_name_id = (SELECT material_name_id FROM material_name WHERE material_name = #{materialName}),
weight = #{weight}
WHERE product_id = #{productId};

mapper.delete:
DELETE IGNORE FROM product_material
WHERE product_id = #{productId}
AND material_name_id = (
SELECT material_name
FROM product_process
WHERE process_order = #{processOrder}
AND product_id = #{productId});

DELETE IGNORE FROM product_process
WHERE product_id = #{productId}
AND process_order = #{processOrder};

mapper.select:
SELECT product_name, description
FROM product
WHERE product_name = #{productName};
