mapper.insert:
INSERT IGNORE INTO `order` (product_id, `count`, status, client_name, client_tel)
SELECT product_id, #{count}, #{status}, #{clientName}, #{clientTel}
FROM `product`
WHERE product_name = #{productName};

mapper.select:
SELECT p.product_name, o.order_id, o.count, o.status, o.client_name, o.client_tel
FROM product AS p, `order` AS o
WHERE p.product_id = o.product_id;

mapper.update:
UPDATE IGNORE `order`
SET status = #{newStatus}
WHERE order_id = #{orderId}

mapper.select:
SELECT p.product_name, o.order_id, o.count, o.status, o.client_name, o.client_tel
FROM product AS p, `order` AS o
WHERE p.product_name = #{productName}
AND p.product_id = o.product_id
AND o.status = 'unsolved';
